import React from "react";

export function CategoryButtons({filterItem, setitem, menuCategory})
{
	const buttons = menuCategory.map( (categoryValue, key) => 
		<button onClick={()=>filterItem(categoryValue)} key={key}>
			{categoryValue}
		</button>);

	return (<div className='d-flex justify-content-center'>{buttons}</div>);
}
